import React, { useState, useRef, useCallback, useEffect } from 'react';
import styled from 'styled-components';
import { Mic, Square, Play, Pause, Download, List } from 'lucide-react';
import { Filesystem, Directory } from '@capacitor/filesystem';
import { Capacitor } from '@capacitor/core';
import { bleAudioDataManager } from '../utils/BleAudioDataManager';

const Container = styled.div`
  background: rgba(255, 255, 255, 0.95);
  border-radius: 20px;
  padding: 2rem;
  margin: 1rem;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
`;

const Title = styled.h2`
  color: #333;
  margin-bottom: 1.5rem;
  text-align: center;
  font-size: 1.5rem;
  font-weight: 600;
`;

const Button = styled.button`
  background: ${props => {
    if (props.recording) return '#e74c3c';
    if (props.playing) return '#f39c12';
    return '#667eea';
  }};
  color: white;
  border: none;
  border-radius: 12px;
  padding: 1rem 2rem;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin: 0.5rem;
  min-width: 140px;
  justify-content: center;

  &:hover {
    background: ${props => {
      if (props.recording) return '#c0392b';
      if (props.playing) return '#e67e22';
      return '#5a67d8';
    }};
    transform: translateY(-2px);
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.2);
  }

  &:disabled {
    background: #ccc;
    cursor: not-allowed;
    transform: none;
    box-shadow: none;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  justify-content: center;
  gap: 1rem;
  flex-wrap: wrap;
  margin: 2rem 0;
`;

const Status = styled.div`
  display: flex;
  align-items: center;
  gap: 0.5rem;
  margin: 1rem 0;
  padding: 1rem;
  border-radius: 12px;
  background: ${props => {
    if (props.recording) return '#fdf2f2';
    if (props.playing) return '#fef9e7';
    return '#f8f9fa';
  }};
  color: ${props => {
    if (props.recording) return '#e74c3c';
    if (props.playing) return '#f39c12';
    return '#666';
  }};
  font-weight: 500;
  justify-content: center;
`;

const Timer = styled.div`
  font-size: 2rem;
  font-weight: bold;
  text-align: center;
  margin: 1rem 0;
  color: #333;
`;

const AudioInfo = styled.div`
  margin-top: 1rem;
  padding: 1rem;
  background: #f8f9fa;
  border-radius: 12px;
  font-family: monospace;
  font-size: 0.9rem;
`;

const AudioRecorder = ({ isConnected, onAudioData, onRecordingComplete, onAudioHandlerRegister, device }) => {
  const [isRecording, setIsRecording] = useState(false);
  const [isPlaying, setIsPlaying] = useState(false);
  const [recordedAudio, setRecordedAudio] = useState(null);
  const [recordingDuration, setRecordingDuration] = useState(0);
  const [audioData, setAudioData] = useState([]);
  const [audioStreamActive, setAudioStreamActive] = useState(false);
  const [packetCount, setPacketCount] = useState(0);
  const [currentSession, setCurrentSession] = useState(null);
  const [recordings, setRecordings] = useState([]);

  const audioBufferRef = useRef([]);
  const startTimeRef = useRef(null);
  const timerRef = useRef(null);
  const audioContextRef = useRef(null);
  const sourceRef = useRef(null);
  const audioHandlerRef = useRef(null);
  const isRecordingRef = useRef(false);
  const packetCountRef = useRef(0);
  const currentSessionRef = useRef(null);

  // Audio configuration (matching auto_pitch_recorder.py)
  const SAMPLE_RATE = 16000;
  const CHANNELS = 1;
  const SAMPLE_WIDTH = 2;

  useEffect(() => {
    if (isRecording) {
      startTimeRef.current = Date.now();
      timerRef.current = setInterval(() => {
        const elapsed = (Date.now() - startTimeRef.current) / 1000;
        setRecordingDuration(elapsed);
      }, 100);
    } else {
      if (timerRef.current) {
        clearInterval(timerRef.current);
        timerRef.current = null;
      }
    }

    return () => {
      if (timerRef.current) {
        clearInterval(timerRef.current);
      }
    };
  }, [isRecording]);

  const createAudioBlob = useCallback((audioData) => {
    // Create WAV file from audio data
    const buffer = new ArrayBuffer(44 + audioData.length * 2);
    const view = new DataView(buffer);

    // WAV header
    const writeString = (offset, string) => {
      for (let i = 0; i < string.length; i++) {
        view.setUint8(offset + i, string.charCodeAt(i));
      }
    };

    writeString(0, 'RIFF');
    view.setUint32(4, 36 + audioData.length * 2, true);
    writeString(8, 'WAVE');
    writeString(12, 'fmt ');
    view.setUint32(16, 16, true);
    view.setUint16(20, 1, true);
    view.setUint16(22, CHANNELS, true);
    view.setUint32(24, SAMPLE_RATE, true);
    view.setUint32(28, SAMPLE_RATE * CHANNELS * SAMPLE_WIDTH, true);
    view.setUint16(32, CHANNELS * SAMPLE_WIDTH, true);
    view.setUint16(34, 16, true);
    writeString(36, 'data');
    view.setUint32(40, audioData.length * 2, true);

    // Write audio data
    let offset = 44;
    for (let i = 0; i < audioData.length; i++) {
      view.setInt16(offset, audioData[i], true);
      offset += 2;
    }

    return new Blob([buffer], { type: 'audio/wav' });
  }, []);

  const startRecording = useCallback(async () => {
    if (!isConnected) {
      alert('Please connect to XIAO board first');
      return;
    }

    console.log('ðŸŽ¤ Starting recording...');
    console.log('ðŸŽ¤ Audio stream active:', audioStreamActive);
    console.log('ðŸŽ¤ Packets received so far:', packetCount);

    try {
      // Start recording session with data manager
      const deviceId = device?.deviceId || 'unknown';
      const sessionInfo = {
        deviceName: device?.name || 'XIAO Board',
        rssi: device?.rssi || 0
      };

      const { sessionId, logFile } = await bleAudioDataManager.startRecordingSession(deviceId, sessionInfo);
      const sessionData = { sessionId, logFile };
      setCurrentSession(sessionData);
      currentSessionRef.current = sessionData;

      setIsRecording(true);
      isRecordingRef.current = true;
      setAudioData([]);
      audioBufferRef.current = [];
      setRecordingDuration(0);
      startTimeRef.current = Date.now();

      console.log('âœ… Recording started with session:', sessionId);
      console.log('ðŸ“ Log file:', logFile);
    } catch (error) {
      console.error('âŒ Failed to start recording session:', error);
      alert('Failed to start recording: ' + error.message);
    }
  }, [isConnected, audioStreamActive, packetCount, device]);

  const stopRecording = useCallback(async () => {
    setIsRecording(false);
    isRecordingRef.current = false;

    console.log(`ðŸ›‘ Stopping recording. Buffer length: ${audioBufferRef.current.length}`);

    if (audioBufferRef.current.length > 0 && currentSession) {
      try {
        // Convert audio data to Int16Array
        const audioArray = new Int16Array(audioBufferRef.current);
        setAudioData(audioArray);

        // Save recording using data manager
        const recordingMetadata = {
          duration: recordingDuration,
          sampleCount: audioArray.length,
          deviceId: device?.deviceId,
          deviceName: device?.name
        };

        const { fileName, metadataFile, recording } = await bleAudioDataManager.saveAudioRecording(
          currentSession.sessionId, 
          audioArray, 
          recordingMetadata
        );

        // Create audio blob for immediate playback
        const audioBlob = createAudioBlob(audioArray);
        setRecordedAudio(audioBlob);

        // Notify parent component
        if (onRecordingComplete) {
          onRecordingComplete(audioArray);
        }

        console.log(`âœ… Recording saved: ${fileName}`);
        console.log(`ðŸ“„ Metadata: ${metadataFile}`);
        console.log(`ðŸŽµ Audio blob created: ${audioBlob.size} bytes`);

        // Refresh recordings list
        await loadRecordings();

      } catch (error) {
        console.error('âŒ Failed to save recording:', error);
        alert('Failed to save recording: ' + error.message);
      }
    } else {
      console.log('âš ï¸ No audio data captured during recording');
      console.log('âš ï¸ Buffer length:', audioBufferRef.current.length);
      console.log('âš ï¸ Current session:', currentSession);
    }

    setCurrentSession(null);
    currentSessionRef.current = null;
  }, [onRecordingComplete, createAudioBlob, currentSession, recordingDuration, device, loadRecordings]);

  const playRecording = useCallback(async () => {
    if (!recordedAudio) return;

    try {
      setIsPlaying(true);

      // Close existing audio context if any
      if (audioContextRef.current && audioContextRef.current.state !== 'closed') {
        await audioContextRef.current.close();
      }

      // Create new audio context
      audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();

      // Resume audio context if suspended (required for user interaction)
      if (audioContextRef.current.state === 'suspended') {
        await audioContextRef.current.resume();
      }

      // Get audio data from blob
      const arrayBuffer = await recordedAudio.arrayBuffer();
      const audioBuffer = await audioContextRef.current.decodeAudioData(arrayBuffer);

      // Create and configure audio source
      sourceRef.current = audioContextRef.current.createBufferSource();
      sourceRef.current.buffer = audioBuffer;
      sourceRef.current.connect(audioContextRef.current.destination);

      // Handle playback end
      sourceRef.current.onended = () => {
        setIsPlaying(false);
        sourceRef.current = null;
      };

      // Start playback
      sourceRef.current.start();
      console.log('Playback started');

    } catch (error) {
      console.error('Playback error:', error);
      setIsPlaying(false);
      alert('Playback failed: ' + error.message);
    }
  }, [recordedAudio]);

  const pauseRecording = useCallback(() => {
    if (sourceRef.current) {
      try {
        sourceRef.current.stop();
        sourceRef.current = null;
        setIsPlaying(false);
        console.log('Playback stopped');
      } catch (error) {
        console.error('Error stopping playback:', error);
        setIsPlaying(false);
      }
    }
  }, []);

  const downloadRecording = useCallback(async () => {
    if (!recordedAudio) return;

    const fileName = `xiao_recording_${new Date().toISOString().replace(/[:.]/g, '-')}.wav`;

    try {
      if (Capacitor.isNativePlatform()) {
        // iOS/Android: Use Capacitor Filesystem
        console.log('ðŸ“± Saving to device filesystem...');

        // Convert blob to base64
        const arrayBuffer = await recordedAudio.arrayBuffer();
        const base64Data = btoa(String.fromCharCode(...new Uint8Array(arrayBuffer)));

        // Write to Documents directory
        const result = await Filesystem.writeFile({
          path: fileName,
          data: base64Data,
          directory: Directory.Documents,
        });

        console.log('âœ… File saved:', result.uri);
        alert(`Recording saved to Documents folder: ${fileName}`);
      } else {
        // Web: Use traditional download
        console.log('ðŸŒ Downloading file...');
        const url = URL.createObjectURL(recordedAudio);
        const a = document.createElement('a');
        a.href = url;
        a.download = fileName;
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        URL.revokeObjectURL(url);
        console.log('âœ… File downloaded');
      }
    } catch (error) {
      console.error('âŒ Download error:', error);
      alert('Failed to save recording: ' + error.message);
    }
  }, [recordedAudio]);

  // Load existing recordings
  const loadRecordings = useCallback(async () => {
    try {
      const recordingFiles = await bleAudioDataManager.listRecordings();
      const metadataFiles = recordingFiles.filter(f => f.name.endsWith('.json'));

      const recordingsData = [];
      for (const file of metadataFiles) {
        const recording = await bleAudioDataManager.getRecording(file.name);
        if (recording) {
          recordingsData.push(recording);
        }
      }

      // Sort by timestamp (newest first)
      recordingsData.sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));
      setRecordings(recordingsData);

      console.log(`ðŸ“ Loaded ${recordingsData.length} recordings`);
    } catch (error) {
      console.error('Error loading recordings:', error);
    }
  }, []);

  // Handle incoming audio data from BLE (continuous stream)
  useEffect(() => {
    const handleAudioData = async (data, audioStream) => {
      console.log(`ðŸŽµ AudioRecorder received data: ${data.length} samples, recording: ${isRecordingRef.current}`);

      // Track that audio stream is active
      setAudioStreamActive(true);
      packetCountRef.current += 1;

      // Update packet count every 10 packets
      if (packetCountRef.current % 10 === 0) {
        setPacketCount(packetCountRef.current);
      }

      // Always capture audio data when recording is active
      // The firmware sends 160 samples every 10ms continuously
      if (isRecordingRef.current && currentSessionRef.current) {
        // Convert Int16Array to regular array for storage
        const samples = Array.from(data);
        audioBufferRef.current.push(...samples);

        // Log audio data to session file
        await bleAudioDataManager.logAudioData(currentSessionRef.current.sessionId, data, {
          bufferLength: audioBufferRef.current.length,
          packetNumber: packetCountRef.current
        });

        console.log(`ðŸ“ Recording: captured ${samples.length} samples, total: ${audioBufferRef.current.length}`);

        // Log every 100 packets to avoid spam
        if (audioBufferRef.current.length % 16000 === 0) {
          console.log(`ðŸŽ¤ Recording: ${audioBufferRef.current.length} samples captured`);
        }
      } else {
        console.log(`â¸ï¸ Audio data received but not recording (${data.length} samples)`);
      }
    };

    audioHandlerRef.current = handleAudioData;

    // Register the handler with the parent component (only once)
    if (onAudioHandlerRegister) {
      console.log('ðŸ“¡ AudioRecorder registering audio handler');
      onAudioHandlerRegister(handleAudioData);
    }
  }, []); // Remove currentSession dependency to prevent re-registration

  // Load recordings on component mount
  useEffect(() => {
    loadRecordings();
  }, [loadRecordings]);

  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = Math.floor(seconds % 60);
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <Container>
      <Title>ðŸŽ¤ Audio Recorder</Title>

      <Status recording={isRecording} playing={isPlaying}>
        {isRecording && (
          <>
            <Mic size={20} />
            Recording from XIAO MIC (BLE)... ({audioStreamActive ? 'Stream Active' : 'No Stream'})
          </>
        )}
        {isPlaying && (
          <>
            <Play size={20} />
            Playing Recording
          </>
        )}
        {!isRecording && !isPlaying && audioData.length === 0 && (
          <>
            <Mic size={20} />
            {audioStreamActive ? `Audio Stream Active (${packetCount} packets)` : 'Ready to Record'}
          </>
        )}
        {!isRecording && !isPlaying && audioData.length > 0 && (
          <>
            <Square size={20} />
            Recording Complete ({audioStreamActive ? 'Stream Active' : 'No Stream'})
          </>
        )}
      </Status>

      {isRecording && (
        <Timer>
          {formatTime(recordingDuration)}
        </Timer>
      )}

      <ButtonGroup>
        {!isRecording ? (
          <Button onClick={startRecording} disabled={!isConnected}>
            <Mic size={20} />
            Start Recording
          </Button>
        ) : (
          <Button onClick={stopRecording} recording>
            <Square size={20} />
            Stop Recording
          </Button>
        )}

        {recordedAudio && (
          <>
            {!isPlaying ? (
              <Button onClick={playRecording}>
                <Play size={20} />
                Play
              </Button>
            ) : (
              <Button onClick={pauseRecording} playing>
                <Pause size={20} />
                Pause
              </Button>
            )}

            <Button onClick={downloadRecording}>
              <Download size={20} />
              Download
            </Button>
          </>
        )}
      </ButtonGroup>

      <AudioInfo>
        <strong>Audio Stream Info:</strong><br />
        Stream Active: {audioStreamActive ? 'Yes' : 'No'}<br />
        Packets Received: {packetCount.toLocaleString()}<br />
        {audioData.length > 0 && (
          <>
            <br /><strong>Recording Info:</strong><br />
            Duration: {formatTime(recordingDuration)}<br />
            Samples: {audioData.length.toLocaleString()}<br />
            Sample Rate: {SAMPLE_RATE} Hz<br />
            Channels: {CHANNELS}<br />
            Size: {(audioData.length * 2 / 1024).toFixed(1)} KB
          </>
        )}
      </AudioInfo>
    </Container>
  );
};

export default AudioRecorder;
