import { Preferences } from '@capacitor/preferences';
import { Filesystem, Directory, Encoding } from '@capacitor/filesystem';
import { Capacitor } from '@capacitor/core';

export class BleAudioDataManager {
  constructor() {
    this.DEVICE_SETTINGS_PREFIX = 'ble_device_';
    this.KNOWN_DEVICES_KEY = 'ble_known_devices';
    this.RECORDING_SESSIONS_KEY = 'recording_sessions';
  }

  // Device Management with Preferences
  async saveDeviceSettings(deviceId, settings) {
    await Preferences.set({
      key: `${this.DEVICE_SETTINGS_PREFIX}${deviceId}`,
      value: JSON.stringify({
        ...settings,
        lastUpdated: new Date().toISOString()
      })
    });
  }

  async getDeviceSettings(deviceId) {
    const { value } = await Preferences.get({
      key: `${this.DEVICE_SETTINGS_PREFIX}${deviceId}`
    });
    return value ? JSON.parse(value) : null;
  }

  async addKnownDevice(deviceId, deviceInfo) {
    // Save to known devices list
    const { value } = await Preferences.get({ key: this.KNOWN_DEVICES_KEY });
    const knownDevices = value ? JSON.parse(value) : [];

    const existingIndex = knownDevices.findIndex((d) => d.id === deviceId);
    const deviceData = {
      id: deviceId,
      ...deviceInfo,
      lastSeen: new Date().toISOString()
    };

    if (existingIndex >= 0) {
      knownDevices[existingIndex] = deviceData;
    } else {
      knownDevices.push(deviceData);
    }

    await Preferences.set({
      key: this.KNOWN_DEVICES_KEY,
      value: JSON.stringify(knownDevices)
    });
  }

  async getKnownDevices() {
    const { value } = await Preferences.get({ key: this.KNOWN_DEVICES_KEY });
    return value ? JSON.parse(value) : [];
  }

  // Audio Recording Session Management
  async startRecordingSession(deviceId, sessionInfo = {}) {
    const sessionId = `session_${deviceId}_${Date.now()}`;
    const session = {
      id: sessionId,
      deviceId,
      startTime: new Date().toISOString(),
      status: 'recording',
      ...sessionInfo
    };

    // Save session info to preferences
    const { value } = await Preferences.get({ key: this.RECORDING_SESSIONS_KEY });
    const sessions = value ? JSON.parse(value) : [];
    sessions.push(session);
    await Preferences.set({
      key: this.RECORDING_SESSIONS_KEY,
      value: JSON.stringify(sessions)
    });

    // Create log file for real-time data
    const logFile = `audio_stream_${sessionId}.log`;
    const header = `# Audio Stream Log for ${deviceId}\n# Session: ${sessionId}\n# Started: ${session.startTime}\n\n`;

    await Filesystem.writeFile({
      path: `audio_logs/${logFile}`,
      data: header,
      directory: Directory.Documents,
      encoding: Encoding.UTF8,
      recursive: true
    });

    return { sessionId, logFile };
  }

  async logAudioData(sessionId, audioData, metadata = {}) {
    const logFile = `audio_stream_${sessionId}.log`;
    const timestamp = new Date().toISOString();

    // Convert Int16Array to readable format
    const samples = Array.from(audioData);
    const audioInfo = {
      timestamp,
      sessionId,
      sampleCount: audioData.length,
      samples: samples.slice(0, 10), // First 10 samples for debugging
      range: {
        min: Math.min(...samples),
        max: Math.max(...samples)
      },
      ...metadata
    };

    const logEntry = `${timestamp} | ${JSON.stringify(audioInfo)}\n`;

    try {
      await Filesystem.appendFile({
        path: `audio_logs/${logFile}`,
        data: logEntry,
        directory: Directory.Documents,
        encoding: Encoding.UTF8
      });
    } catch (error) {
      console.error('Error logging audio data:', error);
    }
  }

  async saveAudioRecording(sessionId, audioData, metadata = {}) {
    const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
    const fileName = `recording_${sessionId}_${timestamp}.wav`;

    // Validate audio data (like Python)
    console.log(`ðŸ” Validating audio data: ${audioData.length} samples`);
    const isValid = this.validateAudioData(audioData);
    if (!isValid) {
      console.warn('âš ï¸ Audio data validation failed, but continuing...');
    }

    // Calculate actual duration and sample rate (like Python)
    const actualDuration = metadata.duration || (audioData.length / 16000);
    const actualSampleRate = this.calculateActualSampleRate(audioData, actualDuration);

    console.log(`ðŸ“Š Audio Analysis:`);
    console.log(`   Samples: ${audioData.length.toLocaleString()}`);
    console.log(`   Duration: ${actualDuration.toFixed(2)}s`);
    console.log(`   Sample Rate: ${actualSampleRate} Hz`);
    console.log(`   Playback Duration: ${(audioData.length / actualSampleRate).toFixed(2)}s`);

    // Create WAV file from audio data with dynamic sample rate
    const wavBlob = this.createWavBlob(audioData, actualDuration);

    // Convert blob to base64 for storage
    const arrayBuffer = await wavBlob.arrayBuffer();
    const base64Data = btoa(String.fromCharCode(...new Uint8Array(arrayBuffer)));

    const recording = {
      sessionId,
      fileName,
      timestamp: new Date().toISOString(),
      sampleCount: audioData.length,
      duration: actualDuration,
      sampleRate: actualSampleRate,
      playbackDuration: audioData.length / actualSampleRate,
      fileSize: wavBlob.size,
      audioQuality: {
        isValid,
        min: Math.min(...Array.from(audioData)),
        max: Math.max(...Array.from(audioData)),
        dynamicRange: Math.max(...Array.from(audioData)) - Math.min(...Array.from(audioData))
      },
      ...metadata
    };

    // Save audio file
    await Filesystem.writeFile({
      path: `recordings/${fileName}`,
      data: base64Data,
      directory: Directory.Documents,
      recursive: true
    });

    // Save recording metadata
    const metadataFile = `recording_${sessionId}_${timestamp}.json`;
    await Filesystem.writeFile({
      path: `recordings/${metadataFile}`,
      data: JSON.stringify(recording, null, 2),
      directory: Directory.Documents,
      encoding: Encoding.UTF8,
      recursive: true
    });

    // Update session status
    await this.updateSessionStatus(sessionId, 'completed', recording);

    return { fileName, metadataFile, recording };
  }

  async updateSessionStatus(sessionId, status, data = {}) {
    const { value } = await Preferences.get({ key: this.RECORDING_SESSIONS_KEY });
    const sessions = value ? JSON.parse(value) : [];

    const sessionIndex = sessions.findIndex(s => s.id === sessionId);
    if (sessionIndex >= 0) {
      sessions[sessionIndex] = {
        ...sessions[sessionIndex],
        status,
        endTime: new Date().toISOString(),
        ...data
      };

      await Preferences.set({
        key: this.RECORDING_SESSIONS_KEY,
        value: JSON.stringify(sessions)
      });
    }
  }

  async getRecordingSessions(deviceId = null) {
    const { value } = await Preferences.get({ key: this.RECORDING_SESSIONS_KEY });
    const sessions = value ? JSON.parse(value) : [];

    return deviceId ? sessions.filter(s => s.deviceId === deviceId) : sessions;
  }

  async listRecordings() {
    try {
      const result = await Filesystem.readdir({
        path: 'recordings',
        directory: Directory.Documents
      });

      return result.files.filter(file => 
        file.name.endsWith('.wav') || file.name.endsWith('.json')
      );
    } catch (error) {
      console.error('Error listing recordings:', error);
      return [];
    }
  }

  async getRecording(metadataFile) {
    try {
      const result = await Filesystem.readFile({
        path: `recordings/${metadataFile}`,
        directory: Directory.Documents,
        encoding: Encoding.UTF8
      });

      return JSON.parse(result.data);
    } catch (error) {
      console.error('Error reading recording metadata:', error);
      return null;
    }
  }

  async playRecording(fileName) {
    try {
      const result = await Filesystem.readFile({
        path: `recordings/${fileName}`,
        directory: Directory.Documents
      });

      // Convert base64 back to blob
      const binaryString = atob(result.data);
      const bytes = new Uint8Array(binaryString.length);
      for (let i = 0; i < binaryString.length; i++) {
        bytes[i] = binaryString.charCodeAt(i);
      }

      return new Blob([bytes], { type: 'audio/wav' });
    } catch (error) {
      console.error('Error reading recording file:', error);
      return null;
    }
  }

  async exportRecording(fileName) {
    if (Capacitor.isNativePlatform()) {
      // On iOS/Android, file is already in Documents directory
      // User can access it through Files app
      return fileName;
    } else {
      // On web, trigger download
      const blob = await this.playRecording(fileName);
      if (blob) {
        const url = URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = fileName;
        document.body.appendChild(a);
        a.click();
        document.body.removeChild(a);
        URL.revokeObjectURL(url);
      }
      return fileName;
    }
  }

  async cleanupOldData(daysToKeep = 7) {
    const cutoffDate = new Date();
    cutoffDate.setDate(cutoffDate.getDate() - daysToKeep);

    // Clean up old sessions
    const sessions = await this.getRecordingSessions();
    const recentSessions = sessions.filter(s => 
      new Date(s.startTime) > cutoffDate
    );

    await Preferences.set({
      key: this.RECORDING_SESSIONS_KEY,
      value: JSON.stringify(recentSessions)
    });

    // Clean up old files
    try {
      const recordings = await this.listRecordings();
      for (const file of recordings) {
        const stat = await Filesystem.stat({
          path: `recordings/${file.name}`,
          directory: Directory.Documents
        });

        if (stat.mtime < cutoffDate.getTime()) {
          await Filesystem.deleteFile({
            path: `recordings/${file.name}`,
            directory: Directory.Documents
          });
          console.log(`Deleted old recording: ${file.name}`);
        }
      }
    } catch (error) {
      console.error('Error cleaning up old data:', error);
    }
  }

  // Helper method to calculate actual sample rate (like Python)
  calculateActualSampleRate(audioData, duration) {
    const calculatedRate = audioData.length / duration;
    if (Math.abs(calculatedRate - 16000) / 16000 < 0.1) {
      return 16000;
    }
    return Math.round(calculatedRate);
  }

  // Helper method to validate audio data (like Python)
  validateAudioData(audioData) {
    const samples = Array.from(audioData);
    const min = Math.min(...samples);
    const max = Math.max(...samples);
    const mean = samples.reduce((a, b) => a + b, 0) / samples.length;
    const rms = Math.sqrt(samples.reduce((a, b) => a + b * b, 0) / samples.length);

    console.log(`ðŸŽµ Audio Quality Analysis:`);
    console.log(`   Min: ${min}, Max: ${max}`);
    console.log(`   Mean: ${mean.toFixed(1)}, RMS: ${rms.toFixed(1)}`);
    console.log(`   Dynamic range: ${max - min}`);

    // Check for silence or clipping (like Python)
    if (max - min < 100) {
      console.warn('âš ï¸ Audio appears to be silent or very quiet');
      return false;
    }
    if (Math.abs(max) > 30000 || Math.abs(min) > 30000) {
      console.warn('âš ï¸ Audio appears to be clipping');
      return false;
    }

    return true;
  }

  // Helper method to create WAV blob with dynamic sample rate
  createWavBlob(audioData, actualDuration = null) {
    const CHANNELS = 1;
    const SAMPLE_WIDTH = 2;

    // Calculate actual sample rate like Python
    let sampleRate = 16000; // Default
    if (actualDuration) {
      sampleRate = this.calculateActualSampleRate(audioData, actualDuration);
      console.log(`ðŸ“Š Using calculated sample rate: ${sampleRate} Hz (duration: ${actualDuration}s)`);
    } else {
      console.log(`ðŸ“Š Using default sample rate: ${sampleRate} Hz`);
    }

    const buffer = new ArrayBuffer(44 + audioData.length * 2);
    const view = new DataView(buffer);

    // WAV header
    const writeString = (offset, string) => {
      for (let i = 0; i < string.length; i++) {
        view.setUint8(offset + i, string.charCodeAt(i));
      }
    };

    writeString(0, 'RIFF');
    view.setUint32(4, 36 + audioData.length * 2, true);
    writeString(8, 'WAVE');
    writeString(12, 'fmt ');
    view.setUint32(16, 16, true);
    view.setUint16(20, 1, true);
    view.setUint16(22, CHANNELS, true);
    view.setUint32(24, sampleRate, true);  // Use calculated sample rate
    view.setUint32(28, sampleRate * CHANNELS * SAMPLE_WIDTH, true);
    view.setUint16(32, CHANNELS * SAMPLE_WIDTH, true);
    view.setUint16(34, 16, true);
    writeString(36, 'data');
    view.setUint32(40, audioData.length * 2, true);

    // Write audio data
    let offset = 44;
    for (let i = 0; i < audioData.length; i++) {
      view.setInt16(offset, audioData[i], true);
      offset += 2;
    }

    return new Blob([buffer], { type: 'audio/wav' });
  }

  async clearAllData() {
    // Clear preferences
    await Preferences.clear();

    // Clear filesystem data
    try {
      await Filesystem.rmdir({
        path: 'audio_logs',
        directory: Directory.Documents,
        recursive: true
      });
      await Filesystem.rmdir({
        path: 'recordings',
        directory: Directory.Documents,
        recursive: true
      });
    } catch (error) {
      console.log('Some directories may not exist:', error);
    }
  }
}

// Export singleton instance
export const bleAudioDataManager = new BleAudioDataManager();
